  async function getblog(){
    const res=await blogdetailid(x);
    // console.log(JSON.parse(res));
    const p=JSON.parse(res);
    setdata(p[0]);


}


useEffect(() => {
  window.scrollTo(0,0);
console.log(x)
getblog();

}, [])


  return (
      <>
<Drawer/>
<div className='Blog-cont'>

