import logo from './logo.svg';
import './App.css';
import {useState} from "react"
import React from 'react';
import {GoogleLogin} from "react-google-login"
import "./si.css"
import { OAuth2Client } from 'google-auth-library';
function SignIn() {


const [imghidden,setimghidden]=useState("false");
const handleLogin = async googleData => {
  const res = await fetch("/api/v1/auth/google", {
      method: "POST",
      body: JSON.stringify({
      token: googleData.tokenId
    }),
    headers: {
      "Content-Type": "application/json"
    }
  })
  const data = await res.json()
  // store returned user somehow
}
// const { OAuth2Client } = require('google-auth-library')
const client = new OAuth2Client(process.env.CLIENT_ID)
server.post("/api/v1/auth/google", async (req, res) => {
    const { token }  = req.body
    const ticket = await client.verifyIdToken({
        idToken: token,
        audience: process.env.CLIENT_ID
    });
    const { name, email, picture } = ticket.getPayload();    
    const user = await db.user.upsert({ 
        where: { email: email },
        update: { name, picture },
        create: { name, email, picture }
    })
    res.status(201)
    res.json(user)
})

  return (
    <div className="main-cont">
  <div className={`img-cont ${imghidden?"":"img-hidden"}`}>

    <img className="hands-img" src="/hands.webp"/>
    <span className="nunito com-tagline"> By community, <br/> forever </span>

   <span className="com-subtitle" > THEN. NOW. ALWAYS.</span>
</div>
<GoogleLogin
    clientId={process.env.REACT_APP_GOOGLE_CLIENT_ID}
    buttonText="Log in with Google"
    onSuccess={handleLogin}
    onFailure={handleLogin}
    cookiePolicy={'single_host_origin'}
/>
<div className="action-cont" hidden >
<span className="nunito stag"> Start the Jouney with people you know </span>



    <div> 


       <button id="google" href="#1" onClick={()=>{
        setimghidden(!imghidden);
       }} >
      <div id="glogo"> 
      <img id="logo" src="./G__Logo.svg"/> 
      </div>
     <span  className=" nunito sin">
  Google Sign in
       </span> 


      </button>


      </div>
      <span className="nunito">
        New to the community? <a href="#sdfd" id="signup-link">
Sign Up
        </a>

        </span>


</div>

      </div>
  );
}

export default SignIn;
